CREATE TABLE "PRODUCT" (
	"idProduct" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nameProduct" VARCHAR(255) NOT NULL,
	"characteristicProduct" VARCHAR(255) NOT NULL,
	"descriptionProduct" TEXT NOT NULL,
	"price" MONEY NOT NULL,
	PRIMARY KEY("idProduct")
);

COMMENT ON TABLE "PRODUCT" IS 'Table of the product';
CREATE INDEX "PRODUCTO_index_0"
ON "PRODUCT" ("idProduct");

CREATE TABLE "STOCK" (
	"idStock" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"currentStock" INTEGER NOT NULL,
	PRIMARY KEY("idStock")
);



ALTER TABLE "PRODUCT"
ADD FOREIGN KEY("idProduct") REFERENCES "STOCK"("idStock")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;