CREATE TABLE "USER" (
	"idUser" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"nameUser" VARCHAR(255) NOT NULL,
	"emailUser" VARCHAR(255) NOT NULL,
	"ageUser" INTEGER NOT NULL,
	"signup_dateUser" DATE NOT NULL,
	"phone_numberUser" INTEGER NOT NULL,
	PRIMARY KEY("idUser")
);




CREATE TABLE "PUBLICATION" (
	"idPublication" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"titlePublication" VARCHAR(255) NOT NULL,
	"contentPublication" TEXT NOT NULL,
	"post_datePublication" DATE NOT NULL,
	PRIMARY KEY("idPublication")
);



ALTER TABLE "USER"
ADD FOREIGN KEY("idUser") REFERENCES "PUBLICATION"("idPublication")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;