CREATE TABLE "CAST" (
	"idCast" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	PRIMARY KEY("idCast")
);




CREATE TABLE "ACTOR" (
	"idActor" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"actor_name" VARCHAR(255) NOT NULL,
	"actor_age" INTEGER NOT NULL,
	"actor_dateBirth" DATE NOT NULL,
	"actor_biography" TEXT NOT NULL,
	PRIMARY KEY("idActor")
);




CREATE TABLE "MOVIE" (
	"idMovie" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"movie_name" VARCHAR(255) NOT NULL,
	"movie_duration" TIME NOT NULL,
	"movio_year" INTEGER NOT NULL,
	"movie_description" TEXT NOT NULL,
	PRIMARY KEY("idMovie")
);



ALTER TABLE "MOVIE"
ADD FOREIGN KEY("idMovie") REFERENCES "CAST"("idCast")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;
ALTER TABLE "ACTOR"
ADD FOREIGN KEY("idActor") REFERENCES "CAST"("idCast")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;