CREATE TABLE "CLIENT" (
	"idClient" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"client_name" VARCHAR(255) NOT NULL,
	"client_email" VARCHAR(255) NOT NULL,
	"client_phoneNumber" INTEGER NOT NULL,
	"client_DNI" INTEGER NOT NULL,
	PRIMARY KEY("idClient")
);




CREATE TABLE "ORDER" (
	"idOrder" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"order_name" VARCHAR(255),
	"order_date" DATE,
	"order_time" TIME,
	PRIMARY KEY("idOrder")
);




CREATE TABLE "ITEM" (
	"idItem" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	PRIMARY KEY("idItem")
);




CREATE TABLE "DISH" (
	"idDish" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"dish_name" VARCHAR(255) NOT NULL,
	"dish_description" TEXT NOT NULL,
	"dish_price" MONEY NOT NULL,
	PRIMARY KEY("idDish")
);




CREATE TABLE "MENU" (
	"idMenu" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"menu_name" VARCHAR(255) NOT NULL,
	"menu_description" TEXT NOT NULL,
	PRIMARY KEY("idMenu")
);




CREATE TABLE "STATE" (
	"idState" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"state" BOOLEAN NOT NULL,
	"state_description" VARCHAR(255),
	PRIMARY KEY("idState")
);



ALTER TABLE "CLIENT"
ADD FOREIGN KEY("idClient") REFERENCES "ORDER"("idOrder")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;
ALTER TABLE "ORDER"
ADD FOREIGN KEY("idOrder") REFERENCES "ITEM"("idItem")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;
ALTER TABLE "DISH"
ADD FOREIGN KEY("idDish") REFERENCES "ITEM"("idItem")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;
ALTER TABLE "DISH"
ADD FOREIGN KEY("idDish") REFERENCES "MENU"("idMenu")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;
ALTER TABLE "DISH"
ADD FOREIGN KEY("idDish") REFERENCES "STATE"("idState")
ON UPDATE SET DEFAULT ON DELETE SET DEFAULT;